package step1;
import java.util.*;
public class Num10950 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = Integer.parseInt(sc.nextLine());
		for( int i = 0; i < n; i++ ) {
			int a = sc.nextInt();
			int b = sc.nextInt();
			System.out.println(a+b);
			sc.nextLine();
		}
	}
}
